mapper.update:
UPDATE contents 
SET is_accepted = 1
WHERE content_id = #{VALUE}

UPDATE support_contents 
SET detail = #{detail},
    team_detail = #{teamDetail},
    plan_detail = #{planDetail}
WHERE content_id = #{contentId}

UPDATE gift
SET criterion= #{criterion},
    gift_list =#{giftList},
    ticket_cnt = #{ticketCnt}
WHERE content_id = #{contentId} AND gift_id=#{giftId}

mapper.select:
SELECT content_id,content_sum, content_name, target, category, start_day, end_day, likes, is_accepted,writer_id
	FROM contents
	WHERE content_name = #{VALUE}

SELECT content_id,content_sum, content_name, target, category, start_day, end_day, likes, is_accepted,writer_id
	FROM contents
	WHERE content_id = #{VALUE}
