mapper.select:
SELECT email
FROM user_info
WHERE email = #{VALUE}

SELECT info_id as infoId, name, nickname
FROM user_info
WHERE email=#{email} and password=#{password}

SELECT CASE WHEN identity IS NULL THEN 2 ELSE identity END AS identity
FROM users
WHERE info_id = #{VALUE}

UNION ALL

SELECT 2 AS identity
FROM dual
WHERE NOT EXISTS (
    SELECT identity FROM users WHERE info_id = #{VALUE}
)

mapper.update:
UPDATE users
SET character = #{character}, interests = #{interests}
WHERE user_id=#{userId}

UPDATE user_info
SET name=#{name}, nickname=#{nickname}, password=#{password}
WHERE info_id=#{infoId}

UPDATE user_info
SET accounts = #{accounts}, bank= #{bank}
WHERE info_id = #{infoId}

UPDATE user_info
SET balance = balance + #{amount}
WHERE info_id = #{infoId}

UPDATE user_info
SET accounts = null, bank = null
WHERE info_id = #{VALUE}

UPDATE users
SET character = #{character}
WHERE user_id = #{userId}
